import logo from './logo.svg';
import './App.css';
import {useState} from 'react';

function App() {
  // const names = ["Gaurav", "Priti", "Prakash", "Robin"];
  // creating array of object
  // const users = [
  //   // {
  //   //   name: "Gaurav", 
  //   //   photo: "https://pbs.twimg.com/profile_images/959291281246642177/52oOzHVF_400x400.jpg",
  //   //   description : "Lorem"
  //   // },
  // ]
  const movie = [
    {
      name: "The Avengers",
      rating: "10",
      summary: "When Thor's evil brother, Loki (Tom Hiddleston), gains access to the unlimited power of the energy cube called the Tesseract, Nick Fury (Samuel L. Jackson), director of S.H.I.E.L.D., initiates a superhero recruitment effort to defeat the unprecedented threat to Earth. Joining Fury's dream team are Iron Man (Robert Downey Jr.), Captain America (Chris Evans), the Hulk (Mark Ruffalo), Thor (Chris Hemsworth), the Black Widow (Scarlett Johansson) and Hawkeye (Jeremy Renner).",
      poster: "https://upload.wikimedia.org/wikipedia/en/8/8a/The_Avengers_%282012_film%29_poster.jpg",
    },
    {
      name: "The Avengers",
      rating: "6",
      summary: "When Thor's evil brother, Loki (Tom Hiddleston), gains access to the unlimited power of the energy cube called the Tesseract, Nick Fury (Samuel L. Jackson), director of S.H.I.E.L.D., initiates a superhero recruitment effort to defeat the unprecedented threat to Earth. Joining Fury's dream team are Iron Man (Robert Downey Jr.), Captain America (Chris Evans), the Hulk (Mark Ruffalo), Thor (Chris Hemsworth), the Black Widow (Scarlett Johansson) and Hawkeye (Jeremy Renner).",
      poster: "https://upload.wikimedia.org/wikipedia/en/8/8a/The_Avengers_%282012_film%29_poster.jpg",
    },
    {
      name: "The Avengers",
      rating: "4",
      summary: "When Thor's evil brother, Loki (Tom Hiddleston), gains access to the unlimited power of the energy cube called the Tesseract, Nick Fury (Samuel L. Jackson), director of S.H.I.E.L.D., initiates a superhero recruitment effort to defeat the unprecedented threat to Earth. Joining Fury's dream team are Iron Man (Robert Downey Jr.), Captain America (Chris Evans), the Hulk (Mark Ruffalo), Thor (Chris Hemsworth), the Black Widow (Scarlett Johansson) and Hawkeye (Jeremy Renner).",
      poster: "https://upload.wikimedia.org/wikipedia/en/8/8a/The_Avengers_%282012_film%29_poster.jpg",
    },
    {
      name: "The Avengers",
      rating: "8",
      summary: "When Thor's evil brother, Loki (Tom Hiddleston), gains access to the unlimited power of the energy cube called the Tesseract, Nick Fury (Samuel L. Jackson), director of S.H.I.E.L.D., initiates a superhero recruitment effort to defeat the unprecedented threat to Earth. Joining Fury's dream team are Iron Man (Robert Downey Jr.), Captain America (Chris Evans), the Hulk (Mark Ruffalo), Thor (Chris Hemsworth), the Black Widow (Scarlett Johansson) and Hawkeye (Jeremy Renner).",
      poster: "https://upload.wikimedia.org/wikipedia/en/8/8a/The_Avengers_%282012_film%29_poster.jpg",
    }
  ]
  return (
    <div className="App">
      <header className="App-header">
        {/* <Msg name="Gaurav"/>
        <Msg name="Priti"/>
        <Msg name="Prakash"/> */}
        {/* {names.map(nm => <Msg name={nm}/>)} */}
        {/* {users.map((ur) => (<Msg name={ur.name} photo={ur.photo} description={ur.description}/>))} */}

        <section className = "movie-list">
          {
            movie.map(({name, rating, summary, poster}) => (
              <Movie
                name = {name}
                rating = {rating}
                summary = {summary}
                poster = {poster}
              />
            ))
          }
        </section>



      </header>
    </div>
  );
  // // function Msg(props) {
  // //   // console.log(props)
  // //   return(
  // //     <div>
  // //       <h1>Hellow, {props.name}!!!</h1>
  // //     </div>
  // //   ) ; 
  // // }
  // function Msg({name, photo, description}) {
  //   return(
  //     <div>
  //       <img 
  //         className="user-photo" 
  //         src={photo}
  //         alt={name}
  //       />
  //       <h1>{name}</h1>
  //       <p>{description}</p>
  //     </div>
  //   ) ; 
  // }
  function Movie({name, rating, summary, poster}) {
    const styles ={
      color: rating < 8 ? "crimson" : "green",
      fontWeight: "bold"
    }
    let summarystyles ={
      color: rating < 0 ? "crimson" : "green"

    }





    const [d, setDisplay] = useState("movie-summary");
    const [btn, Button_Name] = useState("Hide summary");
    const [t_val, Toggle] = useState(0);

    return(
      <div className = "movie-container">
        <img src={poster} alt={name} className = "movie-poster" />
        <div className = "movie-specs">
          <h3 className = "movie-name">{name}</h3>
          <p className = "movie-rating" style={styles}>{rating}</p>
        </div>



        <button onClick={()=> {

          if(t_val==0){

          setDisplay("summary_change");
          Button_Name("Show Summary");
          Toggle(1);
          } 

          else{

         setDisplay("movie-summary");
          Button_Name("Hide summary");
          Toggle(0);
          }

        }}>{btn}</button>

        <p className={d}>{summary}</p>
        <Counter />
      </div>
    )
  }

  // hook is a function - starts with 'use' keyword
  function Counter() {
    // let like = 10;
    // const [state, setState] = useState(InitialValue);
    // state - current scenario - current data
    // state - imp variable; setstate - React state is changed - re-render
    // re-render  - updating your component

    // button click - onClick => set like =>infrom react like is changed
    const [like, setLike] = useState(5);
    const [dislike, setDislike] = useState(10);
    return(
      <div className = "counter-container">
        <button className = "likes-dislikes" onClick = {() => 
          {
            setLike(like + 1); // react will re-render the view
          }
        }>üëç{like}</button>
        <button className = "likes-dislikes" onClick = {() => 
          {
            setDislike(dislike + 1); 
          }
        }>üëé{dislike}</button>
      </div>
    )
  }
}

export default App;
